import React, { useRef, useEffect, useState } from "react";
import * as maptilersdk from "@maptiler/sdk";
import "@maptiler/sdk/dist/maptiler-sdk.css";
import "./map.css";
import axios from 'axios';
import geojson from './quakes.json';

export default function Map() {
  const [quak, setQuak] = useState([]);
  const [markers, setMarkers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://quakify.mcskn.com/backend/api.php');
        setQuak(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  const mapContainer = useRef(null);  
  const ist = { lng: 28.97709, lat: 41.011467 };
  const [zoom] = useState(1.5);

  maptilersdk.config.apiKey = "MTCoW3CI3wRY7K9lPnAI";

  useEffect(() => {
    const map = new maptilersdk.Map({
      container: mapContainer.current,
      style: "https://api.maptiler.com/maps/b89836a1-3239-4824-bf17-5aef32dc5664/style.json?key=MTCoW3CI3wRY7K9lPnAI",
      center: [ist.lng, ist.lat],
      zoom: zoom,
    });

    const newMarkers = quak.map(marker => {
      const child = document.createElement("div");
      child.className = "marker"; 
      child.textContent = marker.magnitude;
      child.style.textAlign = "center";
      child.style.margin = "auto";
      child.style.paddingTop = 22 + "px";
      child.style.color = "white";
      child.style.fontFamily = "inter";
      child.style.fontSize = "15px";
      child.style.alignItems = "center";

      // Dinamik olarak marker boyutu ve stilini ayarla
      const minSize = 20;
      const maxSize = 65;
      const minMag = 3; // minimum beklenen büyüklük
      const maxMag = 8; // maksimum beklenen büyüklük

      // Büyüklüğü 3-8 aralığına normalize et, aralığın dışındaysa sınırla
      const mag = Math.max(minMag, Math.min(marker.magnitude, maxMag));
      // Boyutu lineer olarak büyüklüğe göre hesapla
      const size = minSize + ((mag - minMag) / (maxMag - minMag)) * (maxSize - minSize);

      // Padding ve font boyutunu da büyüklüğe göre ayarla (isteğe bağlı)
      const paddingTop = size * 0.55;
      const fontSize = `${Math.max(10, size * 0.3)}px`;

      setMarkerStyle(child, null, size, 0, fontSize);

      child.addEventListener("mouseover", function () {
        document.getElementById("info").innerHTML =
          marker.city +
          "        " +
          marker.magnitude +
          "   /   " +
          marker.depth +
          "km";
      });

      child.addEventListener("mouseleave", function () {
        document.getElementById("info").innerHTML = "";
      });

      const newMarker = new maptilersdk.Marker({ element: child })
        .setLngLat(marker.coordinates.split(","))
        .addTo(map);

      return newMarker;
    });

    setMarkers(newMarkers);

    return () => {
      map.remove(); 
    };
  }, [ist.lng, ist.lat, zoom, quak]);

  const setMarkerStyle = (child, _, width, paddingTop = 22, fontSize = "15px") => {
    child.style.backgroundImage = "";
    child.style.backgroundColor = "#FF3131"; // Kırmızı iç daire
    child.style.border = "4px solid #fff";   // Beyaz ring
    child.style.boxSizing = "border-box";
    child.style.borderRadius = "50%";
    child.style.width = `${width}px`;
    child.style.height = `${width}px`;
    child.style.display = "flex";
    child.style.alignItems = "center";
    child.style.justifyContent = "center";
    child.style.padding = "0";
    child.style.paddingTop = "0";
    child.style.fontSize = fontSize;
    child.style.color = "white";
    child.style.fontWeight = "bold";
  };

  return (
    <div className="map-wrap">
      <div id="info" className="w-60 left-2 h-8  text-white inter-light mt-1 fixed z-20"></div>
      <div ref={mapContainer} className="map" />
    </div>
  );
}
