import React, { useState } from 'react';
const listMag = '3.0';

const sortQuakes = (quakes, sortType) => {
  const sorted = [...quakes];
  if (sortType === "Largest First") {
    sorted.sort((a, b) => parseFloat(b.magnitude) - parseFloat(a.magnitude));
  } else if (sortType === "Newest First") {
    // Tarih formatı: GG/AA/YYYY
    sorted.sort((a, b) => {
      const [da, ma, ya] = a.date.split('/').map(Number);
      const [db, mb, yb] = b.date.split('/').map(Number);
      const dateA = new Date(ya, ma - 1, da, 0, 0);
      const dateB = new Date(yb, mb - 1, db, 0, 0);
      return dateB - dateA;
    });
  } else if (sortType === "Oldest First") {
    sorted.sort((a, b) => {
      const [da, ma, ya] = a.date.split('/').map(Number);
      const [db, mb, yb] = b.date.split('/').map(Number);
      const dateA = new Date(ya, ma - 1, da, 0, 0);
      const dateB = new Date(yb, mb - 1, db, 0, 0);
      return dateA - dateB;
    });
  }
  return sorted;
};

const RightPanel = ({ quak }) => {
  const [sortType, setSortType] = useState("Largest First");

  const handleSortChange = (e) => {
    setSortType(e.target.value);
  };

  const sortedQuakes = sortQuakes(quak, sortType);

  return (
    <div className='float-right r-0 overflow-hidden bg-[#303030] w-[100%] xl:h-[200px] 2xl:h-[250px]'>
      <p className='ml-2 xl:ml-10 2xl:ml-7 w-[275px] inter text-[#D2D2D2] m-auto pt-12 text-[14pt] xl:text-[16pt] 2xl:text-[20pt]'>Last earthquakes,</p> 
      <div className='ml-2 xl:ml-10 2xl:ml-7 w-[275px] inter text-[#D2D2D2] m-auto text-[14pt] xl:text-[16pt] 2xl:text-[20pt]'>in this 
        <div className='float-right mr-[135px] xl:mr-[125px] 2xl:mr-16' >
          <select className="w-[80px] 2xl:w-[121px] bg-[#202020] text-[#D2D2D2] text-[12pt] 2xl:text-[20pt] xl:h-[30px] 2xl:h-[40px]">
            <option>week</option>
            <option>month</option>
            <option>year</option>
          </select>
        </div>
        <div className='w-[300px] text-[12pt] xl:text-[20px] inter-light'> {quak.length} earthquakes ({listMag}+)</div>
      </div>
      <div className='float-right mr-4 xl:mt-5 2xl:mt-10 mb-2'>
        <select
          className="w-[130px] inter-light bg-[#202020] text-[#D2D2D2] text-[12pt] h-[30px]"
          value={sortType}
          onChange={handleSortChange}
        >
          <option>Largest First</option>
          <option>Newest First</option>
          <option>Oldest First</option>
        </select>
      </div>
      <div className=''>
        <ul>
          {sortedQuakes.slice(0, 10).map((quake) => (
            <li key={quake.id} className="text-[#D2D2D2]">
              {quake.city
                ? quake.city.replace(/[^A-Za-z0-9 ,.-]/g, "")
                : (quake.coordinates || "")}
              {" | "}
              {quake.magnitude}
              {" | "}
              {quake.date}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RightPanel;
