import React from 'react'
const RightPanel = ({ quak, setSortType, selectedMag, setSelectedMag }) => {
  const handleSortChange = (e) => {
    const value = e.target.value;
    if (value === "Largest First") setSortType("largest");
    else if (value === "Newest First") setSortType("newest");
    else if (value === "Oldest First") setSortType("oldest");
  }

  const handleMagChange = (e) => {
    setSelectedMag(parseFloat(e.target.value));
  };

  return (
    <div className='hidden sm:block float-right r-0 overflow-hidden bg-[#303030] w-[100%] xl:h-[200px] 2xl:h-[250px]'>
      <p className='ml-2 xl:ml-10 2xl:ml-7 w-[275px] inter text-[#D2D2D2] m-auto pt-12 text-[14pt] xl:text-[16pt] 2xl:text-[20pt]'>Last earthquakes,</p> 
      <div className='ml-2 xl:ml-10 2xl:ml-7 w-[275px] inter text-[#D2D2D2] m-auto text-[14pt] xl:text-[16pt] 2xl:text-[20pt]'>in this 
        <div className='float-right mr-[135px] xl:mr-[125px] 2xl:mr-16' >
          <select className="w-[80px] pl-1 2xl:w-[121px] bg-[#202020] text-[#D2D2D2] text-[12pt] 2xl:text-[20pt] xl:h-[30px] 2xl:h-[40px]">
            <option>week</option>
            <option>month</option>
            <option>year</option>
            <option>ll</option>
          </select>
        </div>
        <div className='h-[10px]'></div>
        <div className="flex w-[300px] text-[12pt] xl:text-[20px] inter-light">
          <div>{quak.length} earthquakes  </div>
          <div className='w-[10px]'></div>
          <select
            className='w-[65px] pl-1 inter-light bg-[#202020] text-[#D2D2D2] text-[12pt] h-[30px]'
            value={selectedMag}
            onChange={handleMagChange}
          >
            <option value={2.0}>2.0+</option>
            <option value={3.0}>3.0+</option>
            <option value={3.5}>3.5+</option>
            <option value={4.0}>4.0+</option>
            <option value={4.5}>4.5+</option>
            <option value={5.0}>5.0+</option>
          </select>
        </div>

      </div>
      <div className='float-right mr-4 xl:mt-5 2xl:mt-10 mb-2'>
              <select onChange={handleSortChange} className="w-[130px] pl-1  inter-light bg-[#202020] text-[#D2D2D2] text-[12pt] h-[30px]">
                <option>Largest First</option>
                <option>Newest First</option>
                <option>Oldest First</option>
              </select>
      </div>
      <div className='bg-[#000000] p-2 border fixed bottom-2 right-5 2xl:text-[#757575] xl:text-[#757575] inter 2xl:text-sm sm:text-xs sm:text-[#202020] xl:text-xs md:text-[#202020] font-light '>This is a demo project. All data is fictitious.</div>

    </div>
  );
}

export default RightPanel;
