import React from 'react';

const MobilePanel = ({ setShowList }) => {
    const [mapOpen, setMapOpen] = React.useState(false);

    const handleOpenMap = () => {
        if (window.innerWidth > 640) return;
        const mainContent = document.getElementById('main-content');
        if (mainContent && window.innerWidth <= 640) {
            mainContent.classList.remove('hidden');
        }
        setShowList(false);
        setMapOpen(true);
    };

    const handleBackToList = () => {
        if (window.innerWidth > 640) return;
        const mainContent = document.getElementById('main-content');
        if (mainContent && window.innerWidth <= 640) {
            mainContent.classList.add('hidden');
        }
        setShowList(true);
        setMapOpen(false);
    };

    // Harita açıkken sadece geri dön butonunu göster
    if (mapOpen) {
        return (
            <div className="fixed z-30 bottom-0 bg-[#202020] rounded-t-2xl h-[100px] w-full flex justify-center items-center sm:hidden">
                <a
                    className="p-2 inter bg-[#303030] w-[80%] text-white flex text-xl justify-center rounded-2xl"
                    href="#"
                    onClick={handleBackToList}
                >
                    Open Quake List
                </a>
            </div>
        );
    }

    // Normal panel
    return (
        <div className='block sm:hidden'>
            <div className="w-full h-[60px] bg-[#303030] rounded-b-2xl">
                <div className='h-full flex justify-center items-center'>
                    <a className='inter text-white ' href="">Last 100 Quakes</a>
                    <div className='w-14'></div>
                    <a className='inter text-[#909090]' href="">Most Big 100 Quakes</a>
                </div>
            </div>
            <div className="w-full z-10 h-[110px] mt-10 bg-[#303030] rounded-t-2xl"> 
                <div className='flex justify-center top-0 h-[85%] items-center w-full'>
                    <select className='text-[#D2D2D2] text-[12pt] bg-[#202020] rounded-lg inter-light p-2'><option>Lastest First</option></select>
                    <div className='w-10'/>
                    <select className='text-[#D2D2D2] text-[12pt] bg-[#202020] rounded-lg inter-light p-2'><option >This Week</option></select>
                </div>
            </div>
            <div id="bottom panel" className="fixed z-30 bottom-0 bg-[#202020] rounded-t-2xl h-[100px] w-full">
                <div className='h-full flex justify-center items-center'>
                    <a
                        className="p-2 inter bg-[#303030] w-[80%] text-white flex text-xl justify-center rounded-2xl"
                        href="#"
                        onClick={handleOpenMap}
                    >
                        Open Map
                    </a>
                </div>
            </div>
        </div>
    );
};

export default MobilePanel;
