import React from "react";
import { useLocation } from "react-router-dom";

const Navbar = () => {
  const location = useLocation();

  // Aktif sayfa kontrolü
  const isHome = location.pathname === "/";
  const isAbout = location.pathname === "/about";
  const isContact = location.pathname === "/contact";

  return (
    <div className="w-full flex justify-center bg-[#202020]">
      <div className="table w-[900px]">
        <a
          href="/"
          className={`ml-[150px] text-center sm:hidden xl:table-cell align-middle w-[300px] xl:h-[50px] 2xl:h-[71px] ${
            isHome ? "bg-[#303030]" : "bg-[#202020] hover:bg-[#303030]"
          }`}
        >
          <p className="inter text-[#D2D2D2] xl:text-[20pt] 2xl:text-[24pt]">Map</p>
        </a>
        <a
          href="/about"
          className={`text-center sm:hidden xl:table-cell align-middle w-[300px] xl:h-[50px] 2xl:h-[71px] ${
            isAbout ? "bg-[#303030]" : "bg-[#202020] hover:bg-[#303030]"
          }`}
        >
          <p className="inter text-[#D2D2D2] xl:text-[20pt] 2xl:text-[24pt]">About Us</p>
        </a>
        <a
          href="/contact"
          className={`text-center sm:hidden xl:table-cell align-middle w-[300px] xl:h-[50px] 2xl:h-[71px] ${
            isContact ? "bg-[#303030]" : "bg-[#202020] hover:bg-[#303030]"
          }`}
        >
          <p className="inter text-[#D2D2D2] xl:text-[20pt] 2xl:text-[24pt]">Contact</p>
        </a>
        <div className="table-cell w-64 h-1"></div>
      </div>
    </div>
  );
};

export default Navbar;
