import React, { useEffect, useState } from "react";
import '../App.css';
import Component from '../components/Component';
import Navbar from '../components/Navbar';
import Panel from '../components/rightpanel';
import MobilePanel from '../components/MobilePanel';
import Map from '../components/Map';
import axios from 'axios';

const Home = ({sortType, setSortType}) => {
    const [quak, setQuak] = useState([]);
    const [selectedMag, setSelectedMag] = useState(2.0); // Varsayılan 2.0+
    const [showMap, setShowMap] = useState(false); // <-- EKLENDİ

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('https://quakify.mcskn.com/backend/api.php');
                setQuak(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    // "29/12/1981" -> 19811229
    function dateStringToComparable(str) {
        const [day, month, year] = str.split('/');
        return `${year}${month.padStart(2, '0')}${day.padStart(2, '0')}`;
    }

    // Önce filtrele, sonra sırala
    const filteredQuak = quak.filter(q => parseFloat(q.magnitude) >= selectedMag);

    const sortedQuak =  [...filteredQuak].sort((a, b) => {
        if (sortType === "largest") return b.magnitude - a.magnitude;
        if (sortType === "oldest") return dateStringToComparable(a.date) - dateStringToComparable(b.date);
        if (sortType === "newest") return dateStringToComparable(b.date) - dateStringToComparable(a.date);
        return 0; 
    });

    return (
        <div>
            <Navbar />
            <MobilePanel setShowMap={setShowMap} showMap={showMap} />
            {/* SADECE showMap false ise Component göster */}
            {!showMap && (
                <Component className='z-20 mt-[-20px] block sm:hidden overflow-hidden rounded-t-3xl h-[680px]' quak={sortedQuak} />
            )}
            <div id="main" className='float-right w-[20%] sm:w-[40%] md:w-[30%] lg:w-[25%] xl:w-[20%] inline-block'>
              <Panel
                quak={sortedQuak}
                setSortType={setSortType}
                selectedMag={selectedMag}
                setSelectedMag={setSelectedMag}
              />
            </div>
            <Map />
        </div>
    );
};

export default Home;
