import React, { useEffect, useState } from "react";
import '../App.css';
import Component from '../components/Component';
import Navbar from '../components/Navbar';
import Panel from '../components/rightpanel';
import Map from '../components/Map';
import axios from 'axios';

const Home = () => {
    const [quak, setQuak] = useState([]);
  useEffect(() => {

    const fetchData = async () => {
      try {
        const response = await axios.get('https://quakify.mcskn.com/backend/api.php');
        setQuak(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

    const sortedQuak =  [...quak].sort((a, b) => {
      if (sortType === "largest") return b.magnitude - a.magnitude;
      if (sortType === "oldest") return new Date(a.date) - new Date(b.date);
      if (sortType === "newest") return new Date(b.date) - new Date(a.date);
      return 0; 
    });

    return (
        <div>
            <Navbar />
            <div id="main" className='float-right w-[20%] inline-block'>
              <Panel quak={quak} setSortType={setSortType} />
              <Component  quak={sortedQuak} />
            </div>
            <Map id="child"/>
        </div>
    );
};

export default Home;
