import React, { useState, useEffect } from 'react';
import axios from 'axios';
const listMag = '3.0';

const RightPanel = () => {
  const [quak, setQuak] = useState([]);
  const [sortType, setSortType] = useState("largest");
  const [period, setPeriod] = useState("all");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `https://quakify.mcskn.com/backend/api.php?sort=${sortType}&period=${period}`
        );
        setQuak(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, [sortType, period]);

  return (
    <div className='float-right r-0 overflow-hidden bg-[#303030] w-[100%] xl:h-[200px] 2xl:h-[250px]'>
      <p className='ml-2 xl:ml-10 2xl:ml-7 w-[275px] inter text-[#D2D2D2] m-auto pt-12 text-[14pt] xl:text-[16pt] 2xl:text-[20pt]'>Last earthquakes,</p> 
      <div className='ml-2 xl:ml-10 2xl:ml-7 w-[275px] inter text-[#D2D2D2] m-auto text-[14pt] xl:text-[16pt] 2xl:text-[20pt]'>in this 
        <div className='float-right mr-[135px] xl:mr-[125px] 2xl:mr-16' >
          <select
            className="w-[80px] 2xl:w-[121px] bg-[#202020] text-[#D2D2D2] text-[12pt] 2xl:text-[20pt] xl:h-[30px] 2xl:h-[40px]"
            value={period}
            onChange={e => setPeriod(e.target.value)}
          >
            <option value="week">week</option>
            <option value="month">month</option>
            <option value="year">year</option>
            <option value="all">all</option>
          </select>
        </div>
        <div className='w-[300px] text-[12pt] xl:text-[20px] inter-light'> {quak.length} earthquakes ({listMag}+)</div>
      </div>
      <div className='float-right mr-4 xl:mt-5 2xl:mt-10 mb-2'>
        <select
          className="w-[130px] inter-light bg-[#202020] text-[#D2D2D2] text-[12pt] h-[30px]"
          value={sortType}
          onChange={e => setSortType(e.target.value)}
        >
          <option value="largest">Largest First</option>
          <option value="newest">Newest First</option>
          <option value="oldest">Oldest First</option>
        </select>
      </div>
      <div className=''>
        <ul>
          {quak.slice(0, 10).map((quake, idx) => (
            <li
              key={quake.id}
              className={`text-[#D2D2D2] ${idx % 2 === 0 ? "bg-[#303030]" : "bg-[#202020]"}`}
              style={{ padding: "4px 8px" }}
            >
              {quake.city
                ? quake.city.replace(/[^A-Za-z0-9 ,.-]/g, "")
                : (quake.coordinates || "")}
              {" | "}
              {quake.magnitude}
              {" | "}
              {quake.date}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RightPanel;
