import React from 'react';

// Home.js'den gelen proplar: showMap, setShowMap, setShowList
const MobilePanel = ({ showMap, setShowMap, setShowList }) => {

    const handleOpenMap = (event) => {
        event.preventDefault(); // Sayfanın üste kaymasını engellemek için
        if (window.innerWidth > 640) return; // Sadece mobil için çalışsın

        // Eğer `main-content` hala bu şekilde yönetiliyorsa:
        const mainContent = document.getElementById('main-content');
        if (mainContent && window.innerWidth <= 640) {
            mainContent.classList.remove('hidden');
        }

        // Home.js'deki state'leri güncelle
        setShowList(false); // Liste görünürlüğünü kapat (Home.js'de yönetiliyor)
        setShowMap(true);   // Harita görünürlüğünü aç (Home.js'de yönetiliyor)
    };

    const handleBackToList = (event) => {
        event.preventDefault(); // Sayfanın üste kaymasını engellemek için
        if (window.innerWidth > 640) return; // Sadece mobil için çalışsın

        // Eğer `main-content` hala bu şekilde yönetiliyorsa:
        const mainContent = document.getElementById('main-content');
        if (mainContent && window.innerWidth <= 640) {
            mainContent.classList.add('hidden');
        }

        // Home.js'deki state'leri güncelle
        setShowList(true);  // Liste görünürlüğünü aç (Home.js'de yönetiliyor)
        setShowMap(false);  // Harita görünürlüğünü kapat (Home.js'de yönetiliyor)
    };

    // Harita açıkken (showMap true ise) sadece geri dön butonunu göster
    if (showMap) { // Home.js'den gelen showMap prop'unu kullanıyoruz
        return (
            <div className="fixed z-30 bottom-0 bg-[#202020] rounded-t-2xl h-[100px] w-full flex justify-center items-center sm:hidden">
                <a
                    className="p-2 inter bg-[#303030] w-[80%] text-white flex text-xl justify-center rounded-2xl"
                    href="#"
                    onClick={handleBackToList} // Bu fonksiyon setShowMap(false) çağıracak
                >
                    Open Quake List
                </a>
            </div>
        );
    }

    // Harita kapalıyken (showMap false ise) normal paneli göster
    return (
        <div className='block sm:hidden'>
            {/* Üstteki linkler ve filtreler */}
            <div className="w-full h-[60px] bg-[#303030] rounded-b-2xl">
                <div className='h-full flex justify-center items-center'>
                    {/* Bu linkler şu anda bir işlevselliğe sahip değil, isteğe bağlı olarak eklenebilir */}
                    <a className='inter text-white ' href="#">Last 100 Quakes</a>
                    <div className='w-14'></div>
                    <a className='inter text-[#909090]' href="#">Most Big 100 Quakes</a>
                </div>
            </div>
            <div className="w-full z-10 h-[110px] mt-10 bg-[#303030] rounded-t-2xl">
                <div className='flex justify-center top-0 h-[85%] items-center w-full'>
                    {/* Bu select'ler şu anda bir işlevselliğe sahip değil, isteğe bağlı olarak eklenebilir */}
                    <select className='text-[#D2D2D2] text-[12pt] bg-[#202020] rounded-lg inter-light p-2'><option>Lastest First</option><option>Biggest First</option><option>Oldest First</option></select>
                    <div className='w-10'/>
                    <select className='text-[#D2D2D2] text-[12pt] bg-[#202020] rounded-lg inter-light p-2'><option >This Week</option><option >This Month</option><option >This Year</option></select>
                </div>
            </div>

            {/* Alttaki "Open Map" butonu */}
            <div id="bottom panel" className="fixed z-30 bottom-0 bg-[#202020] rounded-t-2xl h-[100px] w-full">
                <div className='h-full flex justify-center items-center'>
                    <a
                        className="p-2 inter bg-[#303030] w-[80%] text-white flex text-xl justify-center rounded-2xl"
                        href="#"
                        onClick={handleOpenMap} // Bu fonksiyon setShowMap(true) çağıracak
                    >
                        Open Map
                    </a>
                </div>
            </div>
        </div>
    );
};

export default MobilePanel;
