import { Position } from "geojson";

/**
 * Average radius of Earth in meters
 */
const EARTH_RADIUS = 6371008.8;

/**
 * Average circumfrence of Earth in meters
 */
const EARTH_CIRCUMFERENCE = 2 * Math.PI * EARTH_RADIUS;

/**
 * Convert a wgs84 longitude to web Mercator X (west-east axis), where westmost X is 0 and eastmost X is 1.
 */
function longitudeToMercatorX(lng: number): number {
  return (180 + lng) / 360;
}

/**
 * Convert a wgs84 latitude to web Mercator Y (north-south axis), where northmost Y is 0 and southmost Y is 1.
 */
function latitudeToMercatorY(lat: number): number {
  return (
    (180 -
      (180 / Math.PI) *
        Math.log(Math.tan(Math.PI / 4 + (lat * Math.PI) / 360))) /
    360
  );
}

/**
 * Convert a wgs84 position into a web Mercator position where north-west is [0, 0] and south-east is [1, 1]
 */
function wgs84ToMercator(position: Position): Position {
  const wrappedPos = wrapWgs84(position);
  return [
    longitudeToMercatorX(wrappedPos[0]),
    latitudeToMercatorY(wrappedPos[1]),
  ];
}

/**
 * Converts a mercator X (west-east axis in [0, 1]) to wgs84 longitude
 */
function mercatorXToLongitude(x: number): number {
  return x * 360 - 180;
}

/**
 * Converts a mercator Y (north-south axis in [0, 1]) to wgs84 latitude
 */
function mercatorYToLatitude(y: number): number {
  const y2 = 180 - y * 360;
  return (360 / Math.PI) * Math.atan(Math.exp((y2 * Math.PI) / 180)) - 90;
}

/**
 * Converts a web Mercator position where north-west is [0, 0] and south-east is [1, 1] into a wgs84
 */
function mercatorToWgs84(position: Position): Position {
  return [mercatorXToLongitude(position[0]), mercatorYToLatitude(position[1])];
}

/**
 * Gives the distance in meters between two positions using the Haversine Formula.
 */
function haversineDistanceWgs84(from: Position, to: Position): number {
  const rad = Math.PI / 180;
  const lat1 = from[1] * rad;
  const lat2 = to[1] * rad;
  const a =
    Math.sin(lat1) * Math.sin(lat2) +
    Math.cos(lat1) * Math.cos(lat2) * Math.cos((to[0] - from[0]) * rad);

  const maxMeters = EARTH_RADIUS * Math.acos(Math.min(a, 1));
  return maxMeters;
}

/**
 * Compute the cumulated distance for each position of an array of positions.
 * For I positions, there are I-1 distance, hence the distance at i corresponds
 * to the distance from the (i-1)th position to ith
 */
function haversineCumulatedDistanceWgs84(positions: Position[]): number[] {
  const cumulatedDistance = Array(positions.length);
  cumulatedDistance[0] = 0;
  const l = cumulatedDistance.length;

  for (let i = 1; i < l; i++) {
    cumulatedDistance[i] =
      haversineDistanceWgs84(positions[i - 1], positions[i]) +
      cumulatedDistance[i - 1];
  }
  return cumulatedDistance;
}

/**
 * Returns a position that has longitude in [-180, 180]
 */
function wrapWgs84(position: Position): Position {
  const lng = position[0];
  const lat = position[1];

  const d = 360;
  const w = ((((lng + 180) % d) + d) % d) - 180;
  const wrapLong = w === -180 ? 180 : w;

  return [wrapLong, lat];
}

/*
 * The circumference at a line of latitude in meters.
 */
export function circumferenceAtLatitude(latitude: number) {
  return EARTH_CIRCUMFERENCE * Math.cos((latitude * Math.PI) / 180);
}

/**
 * From a given mercator coordinate and a zoom level, computes the tile index
 */
function mercatorToTileIndex(
  /**
   * Mercator coordinates (north-west is [0, 0], sourth-east is [1, 1])
   */
  position: Position,
  /**
   * Zoom level
   */
  zoom: number,
  /**
   * Returns integer tile indices if `true` or floating-point values if `false`
   */
  strict: boolean = true,
): Position {
  const numberOfTilePerAxis = 2 ** zoom;

  const fIndex: Position = [
    position[0] * numberOfTilePerAxis,
    position[1] * numberOfTilePerAxis,
  ];

  return strict ? [~~fIndex[0], ~~fIndex[1]] : fIndex;
}

/**
 * From a given wgs84 coordinate and a zoom level, computes the tile index
 */
function wgs84ToTileIndex(
  /**
   * Wgs84 coordinates
   */
  position: Position,
  /**
   * Zoom level
   */
  zoom: number,
  /**
   * Returns integer tile indices if `true` or floating-point values if `false`
   */
  strict: boolean = true,
): Position {
  const merc = wgs84ToMercator(position);
  return mercatorToTileIndex(merc, zoom, strict);
}

/**
 * Converts a degree angle into a radian angle
 */
function toRadians(degrees: number): number {
  return (degrees * Math.PI) / 180;
}

/**
 * Converts a radian angle to a degree angle
 */
function toDegrees(radians: number): number {
  return (radians * 180) / Math.PI;
}

/**
 * Compute an intermediate point between two reference points using the Haversine formula.
 * If ratio is `0`, the returned position is pos1.
 * If ratio is `1`, the returned position is pos2.
 * If ratio is `0.5`, the returned position is halfway pos1 pos2 in distance.
 */
function haversineIntermediateWgs84(
  pos1: Position,
  pos2: Position,
  ratio: number,
): Position {
  const d = haversineDistanceWgs84(pos1, pos2);
  const λ1 = toRadians(pos1[0]);
  const φ1 = toRadians(pos1[1]);
  const λ2 = toRadians(pos2[0]);
  const φ2 = toRadians(pos2[1]);

  const δ = d / EARTH_RADIUS; // Angular distance in radians
  const a = Math.sin((1 - ratio) * δ) / Math.sin(δ);
  const b = Math.sin(ratio * δ) / Math.sin(δ);
  const x = a * Math.cos(φ1) * Math.cos(λ1) + b * Math.cos(φ2) * Math.cos(λ2);
  const y = a * Math.cos(φ1) * Math.sin(λ1) + b * Math.cos(φ2) * Math.sin(λ2);
  const z = a * Math.sin(φ1) + b * Math.sin(φ2);

  const φ3 = Math.atan2(z, Math.sqrt(x * x + y * y));
  const λ3 = Math.atan2(y, x);

  return [toDegrees(λ3), toDegrees(φ3)];
}

export const math = {
  EARTH_RADIUS,
  EARTH_CIRCUMFERENCE,
  longitudeToMercatorX,
  latitudeToMercatorY,
  wgs84ToMercator,
  mercatorXToLongitude,
  mercatorYToLatitude,
  mercatorToWgs84,
  haversineDistanceWgs84,
  wrapWgs84,
  circumferenceAtLatitude,
  mercatorToTileIndex,
  wgs84ToTileIndex,
  toRadians,
  toDegrees,
  haversineIntermediateWgs84,
  haversineCumulatedDistanceWgs84,
};
import QuickLRU from "quick-lru";
import { config } from "./config";

/**
 * Informations about an image
 */
export type PixelData = {
  /**
   * The array of pixels as RGBRGB or RGBARGBA in a row-major order
   */
  pixels: Uint8ClampedArray | Uint8Array;
  /**
   * Width of the image in number of pixels
   */
  width: number;
  /**
   * Height of the image in number of pixels
   */
  height: number;
  /**
   * Number of components per pixel (3 if image is RGB, 4 if image is RGBA)
   */
  components: number;
};

/**
 * Type for the function that decodes an image file ArrayBuffer into valid pixel data
 */
export type BufferToPixelDataFunction = (ArrayBuffer) => Promise<PixelData>;

/**
 * Main properties necessary from a TileJSON
 */
export type TileJSON = {
  scale: string;
  format: string;
  maxzoom: number;
  minzoom: number;
  profile: string;
  description: string;
  attribution: string;
  bounds: [number, number, number, number];
  center: [number, number, number];

  /**
   * TileJSON version
   */
  tilejson: string;
  name: string;
  crs: string;
  crs_wkt: string;
  extent: [number, number, number, number];
  tiles: string[];
};

// The LRU cache for storing tile PixelData
let tileCache = null;

/**
 * Singleton-like function to access the tile cache
 */
export function getTileCache(): QuickLRU<string, PixelData> {
  if (!tileCache) {
    tileCache = new QuickLRU({ maxSize: config.tileCacheSize });
  }
  return tileCache;
}

/**
 * Browser function to decode an image file buffer into valid pixel data
 */
export async function bufferToPixelDataBrowser(
  buff: ArrayBuffer,
): Promise<PixelData> {
  const blob = new Blob([buff]);
  const imageBitmap = await createImageBitmap(blob);

  const canvas = document.createElement("canvas");
  const ctx = canvas.getContext("2d");
  canvas.width = imageBitmap.width;
  canvas.height = imageBitmap.height;
  ctx.drawImage(imageBitmap, 0, 0);

  const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
  return {
    pixels: imageData.data,
    width: canvas.width,
    height: canvas.height,
    components: imageData.data.length / (canvas.width * canvas.height),
  };
}

export function getBufferToPixelDataParser(): BufferToPixelDataFunction {
  if (config.bufferToPixelData) {
    return config.bufferToPixelData;
  }

  if (typeof window !== "undefined") {
    return bufferToPixelDataBrowser;
  }

  throw new Error(
    "An image file buffer to pixel data parser is necessary. Specify it in `config.bufferToPixelData`",
  );
}
import { LineString, MultiLineString, Position } from "geojson";

import { callFetch } from "../callFetch";
import { config } from "../config";
import { defaults } from "../defaults";
import { ServiceError } from "./ServiceError";
import { math } from "./math";
import {
  TileJSON,
  getBufferToPixelDataParser,
  getTileCache,
} from "../tiledecoding";

const terrainTileJsonURL = "tiles/terrain-rgb-v2/tiles.json";
let terrainTileJson: TileJSON = null;

export type ElevationAtOptions = {
  /**
   * Custom MapTiler Cloud API key to use instead of the one in global `config`
   */
  apiKey?: string;

  /**
   * Zoom level to use for the terrain RGB tileset.
   * If not provided, the highest zoom level will be used
   */
  zoom?: number;
};

/**
 * Options for batch elevation lookup
 */
export type ElevationBatchOptions = ElevationAtOptions & {
  /**
   * If provided, a median kernel of the given size will smooth the elevation
   * to reduce very small local variations
   */
  smoothingKernelSize?: number;
};

const customMessages = {
  403: "Key is missing, invalid or restricted",
};

async function fetchTerrainTileJson(apiKey: string): Promise<TileJSON> {
  const endpoint = new URL(terrainTileJsonURL, defaults.maptilerApiURL);
  endpoint.searchParams.set("key", apiKey);
  const urlWithParams = endpoint.toString();

  const res = await callFetch(urlWithParams);
  if (res.ok) {
    terrainTileJson = (await res.json()) as TileJSON;
    return terrainTileJson;
  } else {
    if (!res.ok) {
      throw new ServiceError(res, customMessages[res.status] ?? "");
    }
  }
}

/**
 * Get the elevation at a given position.
 * The returned position is of form [longitude, latitude, altitude]
 */
async function at(
  /**
   * Wgs84 position as [longitude, latitude]
   */
  position: Position,
  /**
   * Options
   */
  options: ElevationAtOptions = {},
): Promise<Position> {
  const apiKey = options.apiKey ?? config.apiKey;

  if (!terrainTileJson) {
    await fetchTerrainTileJson(apiKey);
  }

  const maxZoom = terrainTileJson.maxzoom;
  let zoom = ~~(options.zoom ?? maxZoom);
  if (zoom > maxZoom || zoom < 0) {
    zoom = maxZoom;
  }
  const tileIndex = math.wgs84ToTileIndex(position, zoom, false);

  const tileX = ~~tileIndex[0];
  const tileY = ~~tileIndex[1];

  if (!terrainTileJson.tiles.length) {
    throw new Error("Terrain tileJSON tile list is empty.");
  }

  const tileID = `terrain_${zoom.toString()}_${tileX.toString()}_${tileY.toString()}`;
  let tilePixelData;

  const cache = getTileCache();

  if (cache.has(tileID)) {
    tilePixelData = cache.get(tileID);
  } else {
    const tileURL = terrainTileJson.tiles[0]
      .replace("{x}", tileX.toString())
      .replace("{y}", tileY.toString())
      .replace("{z}", zoom.toString());

    const tileRes = await callFetch(tileURL);

    if (!tileRes.ok) {
      throw new ServiceError(tileRes, customMessages[tileRes.status] ?? "");
    }

    const tileBuff = await tileRes.arrayBuffer();
    const tileParser = getBufferToPixelDataParser();
    tilePixelData = await tileParser(tileBuff);
    cache.set(tileID, tilePixelData);
  }

  const pixelX = ~~(tilePixelData.width * (tileIndex[0] % 1));
  const pixelY = ~~(tilePixelData.height * (tileIndex[1] % 1));
  const pixelDataIndex =
    (pixelY * tilePixelData.width + pixelX) * tilePixelData.components;
  const R = tilePixelData.pixels[pixelDataIndex];
  const G = tilePixelData.pixels[pixelDataIndex + 1];
  const B = tilePixelData.pixels[pixelDataIndex + 2];
  const elevation = -10000 + (R * 256 * 256 + G * 256 + B) * 0.1;

  return [position[0], position[1], elevation];
}

/**
 * Perform a batch elevation request
 */
async function batch(
  /**
   * Wgs84 positions as [[lng0, lat0], [lng1, lat1], [lng2, lat2], ...]
   */
  positions: Position[],
  /**
   * Options
   */
  options: ElevationBatchOptions = {},
): Promise<Position[]> {
  const apiKey = options.apiKey ?? config.apiKey;

  if (!terrainTileJson) {
    await fetchTerrainTileJson(apiKey);
  }

  // Better throw about not bein able to parse tiles before fetching them
  const tileParser = getBufferToPixelDataParser();
  const tileURLSchema = terrainTileJson.tiles[0];
  const cache = getTileCache();

  const maxZoom = terrainTileJson.maxzoom;
  let zoom = ~~(options.zoom ?? maxZoom);
  if (zoom > maxZoom || zoom < 0) {
    zoom = maxZoom;
  }
  const tileIndicesFloats = positions.map((position) =>
    math.wgs84ToTileIndex(position, zoom, false),
  );
  const tileIndicesInteger = tileIndicesFloats.map((index) => [
    ~~index[0],
    ~~index[1],
  ]);
  const tileIDs = tileIndicesInteger.map(
    (index) =>
      `terrain_${zoom.toString()}_${index[0].toString()}_${index[1].toString()}`,
  );

  // unique tiles to fetch (excluding those already in cache and the doublons)
  const uniqueTilesToFetch = Array.from(
    new Set(tileIDs.filter((tileID) => !cache.has(tileID))),
  ).map((tileID) => tileID.split("_").slice(1));
  const tileURLs = uniqueTilesToFetch.map((zxy) =>
    tileURLSchema
      .replace("{x}", zxy[1].toString())
      .replace("{y}", zxy[2].toString())
      .replace("{z}", zxy[0].toString()),
  );
  const promisesFetchTiles = tileURLs.map((url) => callFetch(url));
  const resTiles = await Promise.allSettled(promisesFetchTiles);
  const fulfilledRes = resTiles
    .map((el: PromiseSettledResult<Response>) =>
      el.status === "fulfilled" ? el.value : null,
    )
    .filter((res) => res);
  const fulfilledRButNotOkRes = fulfilledRes.filter((res) => !res.ok);

  if (fulfilledRes.length !== promisesFetchTiles.length) {
    throw new Error("Some tiles could not be fetched.");
  }

  if (fulfilledRButNotOkRes.length) {
    throw new ServiceError(
      fulfilledRButNotOkRes[0],
      customMessages[fulfilledRButNotOkRes[0].status] ?? "",
    );
  }

  const tileArrayBuffers = await Promise.all(
    fulfilledRes.map((res) => res.arrayBuffer()),
  );

  // It is possible that the tile is missing
  if (!tileArrayBuffers.every((buff) => buff.byteLength > 0)) {
    throw new Error("Some tiles are not available.");
  }

  const tilePixelDatas = await Promise.all(
    tileArrayBuffers.map((buff) => tileParser(buff)),
  );

  // Adding to cache
  tilePixelDatas.forEach((tilePixelData, i) => {
    const zxy = uniqueTilesToFetch[i];
    const tileID = `terrain_${zxy[0].toString()}_${zxy[1].toString()}_${zxy[2].toString()}`;
    cache.set(tileID, tilePixelData);
  });

  const elevatedPositions = positions.map((position, i) => {
    const tileID = tileIDs[i];
    const tileIndexFloat = tileIndicesFloats[i];
    const tilePixelData = cache.get(tileID);

    // const pixelX = ~~(tilePixelData.width * (tileIndexFloat[0] % 1));
    // const pixelY = ~~(tilePixelData.height * (tileIndexFloat[1] % 1));

    const pixelX = Math.min(
      Math.round(tilePixelData.width * (tileIndexFloat[0] % 1)),
      tilePixelData.width - 1,
    );
    const pixelY = Math.min(
      Math.round(tilePixelData.height * (tileIndexFloat[1] % 1)),
      tilePixelData.height - 1,
    );

    const pixelDataIndex =
      (pixelY * tilePixelData.width + pixelX) * tilePixelData.components;
    const R = tilePixelData.pixels[pixelDataIndex];
    const G = tilePixelData.pixels[pixelDataIndex + 1];
    const B = tilePixelData.pixels[pixelDataIndex + 2];
    const elevation = -10000 + (R * 256 * 256 + G * 256 + B) * 0.1;

    return [position[0], position[1], ~~(elevation * 1000) / 1000];
  });

  // Smoothing
  if (options.smoothingKernelSize) {
    // make sure the kernel is of an odd size
    const kernelSize = ~~(options.smoothingKernelSize / 2) * 2 + 1;
    const elevations: number[] = elevatedPositions.map((pos) => pos[2]);
    const kernelSpan = ~~(kernelSize / 2);

    for (let i = kernelSpan; i < elevations.length - kernelSpan - 1; i += 1) {
      let sum = 0;
      for (let j = 0; j < kernelSize; j += 1) {
        const elev = elevations[i - kernelSpan + j];
        sum += elev;
      }
      sum /= kernelSize;
      elevatedPositions[i][2] = sum;
    }
  }

  return elevatedPositions;
}

/**
 * Creates a clone of a GeoJSON LineString (deep copy with structuredClone) that contains the computed elevation
 * as the third element of each position array ([lng, lat, alt])
 */
async function fromLineString(
  /**
   * A GeoJSON LineStriung feature
   */
  ls: LineString,
  /**
   * Options
   */
  options: ElevationBatchOptions = {},
): Promise<LineString> {
  if (ls.type !== "LineString") {
    throw new Error("The provided object is not a GeoJSON LineString");
  }

  const clone = structuredClone(ls) as LineString;
  const elevatedPositions = await batch(clone.coordinates, options);
  clone.coordinates = elevatedPositions;

  return clone;
}

/**
 * Creates a clone of a MultiLineString (deep copy with structuredClone) that contains the computed elevation
 * as the third element of each position array ([lng, lat, alt])
 */
async function fromMultiLineString(
  /**
   * A GeoJSON LineStriung feature
   */
  ls: MultiLineString,
  /**
   * Options
   */
  options: ElevationBatchOptions = {},
): Promise<MultiLineString> {
  if (ls.type !== "MultiLineString") {
    throw new Error("The provided object is not a GeoJSON MultiLineString");
  }

  const clone = structuredClone(ls) as MultiLineString;
  const multiLengths = clone.coordinates.map((poss) => poss.length);

  // This is equivalent to a batch of batch, so we makes the multilinestring a unique
  // line string to prevent batch to fetch multiple times the same tile
  const flattenPositions = clone.coordinates.flat();
  const flattenPositionsElevated = await batch(flattenPositions, options);

  // And then chopping back into a multi line string
  const result: Position[][] = [];
  let index = 0;
  for (const length of multiLengths) {
    result.push(flattenPositionsElevated.slice(index, index + length));
    index += length;
  }

  clone.coordinates = result;
  return clone;
}

export const elevation = {
  at,
  batch,
  fromLineString,
  fromMultiLineString,
};
